import React, { useState, useEffect, useContext } from "react";
import { Products } from "./Products";
// import { PostForm } from '../Posts/PostForm'
import { IProduct } from "../Types/Product";
import axios from "axios";
import { ProductCard } from "./ProductCard";
import AuthContext from "../context/productContext";
import FilterContext from "../context/FilterContext";
export interface IProductListProps {
  setProductCount: (arg: number) => void;
  showBox: boolean;
  showList: boolean;
}
export const ProductList: React.FC<IProductListProps> = ({
  setProductCount,
  showBox,
  showList,
}) => {
  const {
    minPrice,
    setMinPrice,
    maxPrice,
    setMaxPrice,
    MOQ,
    setMOQ,
    stock_available,
    setStockAvailable,
    selectedCategories,
    setSelectedCategories,
    selectedStock,
    setSelectedStock,
    selectCategory,
    setSelectCategory,
    subCategory,
    setSubCategory,
    stockIn,
    setStockIn,
    countProduct,
    setCountProduct,
    query,
    setQuery,
  } = useContext(FilterContext);
  const [products, setProducts] = useState<IProduct[]>([]);

  const authCxt = useContext(AuthContext);
  // useEffect(() => {
  //   getProducts();
  // })
  useEffect(() => {
    console.log("------------------");
    // getProducts();
    if (authCxt.getProducts.length <= 18 || authCxt.getProducts.length == 0) {
      getProducts();
    }
    console.log(
      authCxt.getProducts,
      "products......................................."
    );
    setProductCount(authCxt.getProducts.length);
  }, [
    minPrice,
    maxPrice,
    MOQ,
    stock_available,
    selectedCategories,
    selectedStock,
    selectCategory,
  ]);
  useEffect(() => {
    setProductCount(authCxt.getProducts.length);
  });
  const getProducts = async () => {
    try {
      let url = "";

      console.log("------------------");
      url = `?minPrice=${minPrice}&maxPrice=${maxPrice}&MOQ=${MOQ}&stock_in_usa=${stock_available}
               &selectedCategories=${encodeURIComponent(
                 JSON.stringify(selectedCategories)
               )}
               &selectedStock=${JSON.stringify(
                 selectedStock
               )}&select_categories=${encodeURIComponent(
                selectCategory
              )}&searchCategory=${subCategory}`;

      const response = await axios.get(
        `http://localhost:3000/api/v1/products` + url
      );
      // debugger
      const data = response.data;
      console.log("------------------", response);

      authCxt.setProducts(response.data.products);
      setProducts(authCxt.getProducts);
      setProductCount(authCxt.getProducts.length);
      setStockIn(response.data.stock);
      setCountProduct(response.data.products);
      if (!query) {
        authCxt.setProducts(response.data.products);
      } else {
        authCxt.setProducts(
          response.data.products.filter((data: any) =>
            data.product_name.toLowerCase().includes(query.toLowerCase())
          )
        );
      }
    } catch (error: any) {
      console.log(error);
    }
  };

  return (
    <>
      {authCxt.getProducts !== null ? (
        <>
          <h1></h1>
          <div className="row">
            {/* setProductCount(products.length) */}
            {authCxt.getProducts.map((product: IProduct) =>
              // if (search == "" || crypto.name.toLowerCase().includes(search.toLowerCase())) {}
              showBox ? (
                <Products
                  key={product.id}
                  product_name={product.product_name}
                  images={product.images}
                  stock_in_USA={product.stock_in_USA}
                  minimum_order_quantity={product.minimum_order_quantity}
                  price={product.price}
                  product_certification={product.product_certification}
                  stock_in={product.stock_in}
                />
              ) : (
                <div></div>
              )
            )}
            {authCxt.getProducts.map((product: IProduct) =>
              showList ? (
                <ProductCard
                  key={product.id}
                  product_name={product.product_name}
                  images={product.images}
                  stock_in_USA={product.stock_in_USA}
                  minimum_order_quantity={product.minimum_order_quantity}
                  price={product.price}
                  product_certification={product.product_certification}
                  stock_in={product.stock_in}
                />
              ) : (
                <div></div>
              )
            )}
            {/* {showList} ? null : null */}
          </div>
        </>
      ) : (
        <h3>Products not found</h3>
      )}
    </>
  );
};
