import React, { useState, useEffect, useContext } from "react";
import axios from "axios";
import { IProduct } from "../../Types/Product";
import AuthContext from "../../context/productContext";
import FilterContext from "../../context/FilterContext";

export const ProductFilter = () => {
  const {
    minPrice,
    setMinPrice,
    maxPrice,
    setMaxPrice,
    MOQ,
    setMOQ,
    stock_available,
    setStockAvailable,
    selectedCategories,
    setSelectedCategories,
    selectedStock,
    setSelectedStock,
    selectCategory,
    setSelectCategory,
    subCategory,
    setSubCategory,
    stockIn,
    setStockIn,
    countProduct,
    setCountProduct,
    query,
    setQuery,
  } = useContext(FilterContext);
  // const [products, setProducts] = useState<IProduct[]>([])
  // const [minPrice, setMinPrice] = useState<string>("");
  // const [maxPrice, setMaxPrice] = useState<string>("");
  // const [MOQ, setMOQ] = useState<string>("");
  // const [stock_available, setStockAvailable] = useState<string>("false");
  const [checked, setChecked] = useState<boolean>(false);
  const [subCategories, setSubCategories] = useState<any>([]);
  const [parentCategories, setParentCategories] = useState<any>([]);
  // // const [productCertification, setProductCertification]=useState<string[]>([]);
  // // const [pc, setPc] = useState<string[]>([])
  // // const [certificationProduct, setCertificationProduct] = useState<IProduct[]>([]);
  // const [selectedCategories , setSelectedCategories ] = useState<any>([]);
  // const [stockIn , setStockIn] = useState<any>([]);
  // const [selectedStock , setSelectedStock ] = useState<any>([]);
  const authCxt = useContext(AuthContext);
  // const [countProduct, setCountProduct] = useState<IProduct[]>([]);
  // const [countOfProduct, setCountOfProduct] = useState<IProduct[]>([]);
  let count: any;
  let category_count: any;
  // useEffect(() => {
  //   getProducts();
  //   // getProductsToCount();
  // }, [minPrice, maxPrice, MOQ, stock_available, selectedCategories, selectedStock]);

  useEffect(() => {
    getCategories();
    handleCheck();
    // getProductsToCount()
  }, [minPrice,
    maxPrice,
    MOQ,
    stock_available,
    selectedCategories,
    selectedStock,
    selectCategory,
    query
  ]);
  // const getProducts = async () => {
  //   try {
  //     // let url = "";
  //     // if (minPrice.length !== 0 || maxPrice.length !== 0 || MOQ || checked || selectedCategories.length > 0 || selectedStock.length > 0) {
  //     //   url = `?minPrice=${minPrice}&maxPrice=${maxPrice}&MOQ=${MOQ}&stock_in_usa=${stock_available}
  //     //          &selectedCategories=${encodeURIComponent(JSON.stringify(selectedCategories))}
  //     //          &selectedStock=${JSON.stringify(selectedStock)}`;
  //     // }
  //     const response = await axios.get(
  //       `http://localhost:3000/api/v1/products`
  //     );
  //     authCxt.setProducts(response.data.products);
  //     setStockIn(response.data.stock)
  //     setCountProduct(response.data.products)
  //     console.log("stockIn",stockIn)
  //     console.log("in product filter file ");
  //     console.log(authCxt.getProducts)
  //     // setProducts(authCxt.getProducts)
  //   } catch (error: any) {
  //     console.log(error);
  //   }
  // };

  // const getProductsToCount = async () => {
  //   try {
  //     const response = await axios.get(`http://localhost:3000/api/v1/products`);
  //     // setCountOfProduct(response.data.products)
  //   }catch (error: any) {
  //     console.log(error);
  //   }
  // };
  let arr1: any = [];
  // const passCertification=(e: React.ChangeEvent<HTMLInputElement>) => {
  //   // handleCheck()
  //   debugger
  //   pc.includes(e.target.value)
  //     ? setPc(
  //       pc.filter((prodCertification) => prodCertification !== e.target.value)
  //       )
  //     : setPc([...pc, e.target.value]);
  //     console.log("pc", pc)
  // }
  const handleCheck = () => {
    authCxt.getProducts.map(
      (product: any, index) => (arr1[index] = product.category_name)
    );
    return removeDup(arr1);
  };

  const removeDup = (data: []) => {
    let arr2: any = [];
    arr2 = data.filter((item, index) => data.indexOf(item) === index)
    setSubCategories(arr2);
    console.log("productCertification",subCategories)
  };

  // const handleStock = () => {
  //   // debugger
  //   products.map(
  //     (product, index) => (arr1[index] = product.stock_in)
  //   );
  //   return removeDup(arr1);
  // };

  // const removeDup = (data: []) => {
  //   let arr2: any = [];
  //   arr2 = data.filter((item, index) => data.indexOf(item) === index)
  //   setStockIn(arr2);
  //   console.log("stockIn",stockIn)
  // };

  const getCategories = async () => {
    try {
      const response = await axios.get(
        `http://localhost:3000/api/v1/categories`
      );
      const data = response.data;
      // debugger;
      // setSubCategories(data.sub_categories);
      // debugger
      setParentCategories(data.parent_categories);
      // getProducts();
    } catch (error: any) {
      console.log(error);
    }
  };

  // const handleSelectedCategories = (e: React.ChangeEvent<HTMLInputElement>) => {
  //   selectedCategories.includes(e.target.value)
  //     ? setSelectedCategories(
  //         selectedCategories.filter((category: any) => category !== e.target.value)
  //       )
  //     : setSelectedCategories([...selectedCategories, e.target.value]);
  //   console.log("selected categories -> ", selectedCategories);
  // };

  const handleChange = () => {
    if (checked) {
      setStockAvailable("false");
      setChecked(false);
    } else {
      setChecked(true);
      setStockAvailable("true");
    }
    console.log(
      "@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@"
    );
    console.log(stock_available);
  };

  return (
    <div>
      <h4>Price</h4>
      <div className="d-flex flex-row mt-2">
        <input
          type="text"
          placeholder="From"
          value={minPrice}
          // ref={inputRef1}
          inputMode="numeric"
          onChange={(e) => {
            setMinPrice(e.target.value);
            // getProducts();
          }}
          className="w-25 rounded-pill"
        />
        <hr className="mx-2" style={{ width: "15px" }}></hr>
        <input
          type="text"
          placeholder="To"
          value={maxPrice}
          // ref={inputRef2}
          inputMode="numeric"
          onChange={(e) => {
            setMaxPrice(e.target.value);
            // getProducts();
          }}
          className="input__box w-25 rounded-pill"
        />
      </div>
      <div className="mt-4">
        <h4>MOQ</h4>
        <input
          type="text"
          placeholder="Less Than"
          value={MOQ}
          // ref={inputMOQ}
          inputMode="numeric"
          onChange={(e) => {
            setMOQ(e.target.value);
            // getProducts();
          }}
          className="input__box w-100 py-1 rounded-pill"
        />
      </div>
      {/* <div className="mt-4">
          <h4>Product Certification</h4>
          {productCertification.map((pCertification)=>{
            return (
              <div className="form-check m-2" > 
              <input 
                className="form-check-input" 
                type="checkbox" 
                value={pCertification}
                onChange={(e) => {
                  handleCheck()
                  passCertification(e)
                }}
              >
              </input>
              {pCertification}
              </div>
          )})}
        </div> */}
      <div className="mt-4">
        <h4>Product Category</h4>
        {subCategories.map((sCategory: any) => {
          // sCategory.map((s: any) =>
            <div className="form-check m-2">
              <input
                className="form-check-input"
                type="checkbox"
                value={sCategory}
                onChange={(e) => {
                  selectedCategories.includes(e.target.value)
                    ? setSelectedCategories(
                        selectedCategories.filter(
                          (category: any) => category !== e.target.value
                        )
                      )
                    : setSelectedCategories([
                        ...selectedCategories,
                        e.target.value,
                      ]);
                  console.log("selected categories -> ", selectedCategories);
                  // getProducts()
                }}
              ></input>
              {sCategory}
              {/* <span style={{marginLeft: "10px"}}>({category_count = parentCategories.filter((pCategory: any) => pCategory.parent_categories === pCategory).length})</span> */}
            </div>
          })}
      </div>
      <div className="mt-4">
        <h4>Stock Avalibility</h4>
        <div className="form-check m-2">
          <input
            className="form-check-input"
            type="checkbox"
            // defaultChecked={stock_available}
            value={stock_available}
            checked={checked}
            onChange={() => {
              handleChange();
              // getProducts();
            }}
          />
          <div className="d-flex align-items-center">
            <div className="d-flex align-items-center">
              <img
                className="img-USA"
                width="24"
                height="24"
                src="	https://thebuyhive.com/buy/img/usa.cbfe8d83.svg"
              />
            </div>
            <div className="" style={{ fontSize: "0.75rem" }}>
              {" "}
              in USA{" "}
            </div>
          </div>
        </div>
      </div>

      <div className="mt-4">
        <h4>Stock Available In</h4>
        {stockIn.map((stock_in: any) => {
          return (
            <div className="form-check m-2">
              <input
                className="form-check-input"
                type="checkbox"
                value={stock_in}
                onChange={(e) => {
                  // debugger
                  selectedStock.includes(e.target.value)
                    ? setSelectedStock(
                        selectedStock.filter(
                          (stock: any) => stock !== e.target.value
                        )
                      )
                    : setSelectedStock([...selectedStock, e.target.value]);
                  // debugger
                  console.log("selected categories -> ", selectedStock);
                  // getProducts()
                }}
              ></input>
              {stock_in}
              {/* {countOfProduct[`${stock_in}`].map((item: any) => item)} */}
              <span style={{ marginLeft: "10px" }}>
                (
                {
                  (count = countProduct.filter(
                    (product: any) => product.stock_in === stock_in
                  ).length)
                }
                )
              </span>
            </div>
          );
        })}
      </div>
      {/* {countOfProduct.map((item: any) => item)}  */}
    </div>
  );
};

export default ProductFilter;
