import React, { useState, useEffect, useContext } from "react";
import { Products } from "./Products";
// import { PostForm } from '../Posts/PostForm'
import { IProduct } from "../Types/Product";
import axios from "axios";
import { ProductCard } from "./ProductCard";
import AuthContext from "../context/productContext";
import FilterContext from "../context/FilterContext";
// import Pagination from '@material-ui/lab/Pagination';
import Pagination from 'react-paginate';

export interface IProductListProps {
  setProductCount: (arg: number) => void;
  showBox: boolean;
  showList: boolean;
}
export const ProductList: React.FC<IProductListProps> = ({
  setProductCount,
  showBox,
  showList,
}) => {
  const {
    minPrice,
    setMinPrice,
    maxPrice,
    setMaxPrice,
    MOQ,
    setMOQ,
    stock_available,
    setStockAvailable,
    selectedCategories,
    setSelectedCategories,
    selectedStock,
    setSelectedStock,
    selectCategory,
    setSelectCategory,
    subCategory,
    setSubCategory,
    stockIn,
    setStockIn,
    countProduct,
    setCountProduct,
    query,
    setQuery,
  } = useContext(FilterContext);

  const [products, setProducts] = useState<IProduct[]>([]);

  const authCxt = useContext(AuthContext);

  handlePageClick = (data) => {
    const selected = data.selected;
    const pageOfData = this.state.allData.slice(selected * this.state.perPage, (selected + 1) * this.state.perPage);

    this.setState({
      pageOfData: pageOfData,
      pageNumber: selected,
    });
  }

  useEffect(() => {
    if (authCxt.getProducts.length <= 18 || authCxt.getProducts.length == 0) {
      getProducts();
    }
    console.log(authCxt.getProducts);
    setProductCount(authCxt.getProducts.length);
  }, [
    minPrice,
    maxPrice,
    MOQ,
    stock_available,
    selectedCategories,
    selectedStock,
    selectCategory,
    query,
    subCategory,
  ]);

  useEffect(() => {
    setProductCount(authCxt.getProducts.length);
  });

  const getProducts = async () => {
    try {
      let url = "";

      console.log("------------------");
      url = `?minPrice=${minPrice}&maxPrice=${maxPrice}&MOQ=${MOQ}&stock_in_usa=${stock_available}
               &selectedCategories=${encodeURIComponent(
                 JSON.stringify(selectedCategories)
               )}
               &selectedStock=${JSON.stringify(
                 selectedStock
               )}&select_categories=${encodeURIComponent(
        selectCategory
      )}`;

      const response = await axios.get(
        `http://localhost:3000/api/v1/products` + url
      );
      const data = response.data;
      console.log("------------------", response);

      if (!query) {
        authCxt.setProducts(response.data.products);
      } else {
        authCxt.setProducts(
          response.data.products.filter((data: any) =>
            data.product_name.toLowerCase().includes(query.toLowerCase())
          )
        );
      }
      setProducts(authCxt.getProducts);
      setCountProduct(response.data.products); // side bar filter stock product count
      setProductCount(authCxt.getProducts.length); // total product count
      setStockIn(response.data.stock);
      // setCountProduct(response.data.products);
    } catch (error: any) {
      console.log(error);
    }
  };

  return (
    <>
      {authCxt.getProducts.length !== 0 ? (
        <>
          <h1></h1>
          <div className="row">
            {authCxt.getProducts.map((product: IProduct) =>
              showBox ? (
                <Products
                  key={product.id}
                  product_name={product.product_name}
                  images={product.images}
                  stock_in_USA={product.stock_in_USA}
                  minimum_order_quantity={product.minimum_order_quantity}
                  price={product.price}
                  product_certification={product.product_certification}
                  stock_in={product.stock_in}
                  category_name={product.category_name}
                />
              ) : (
                <div></div>
              )
            )}
            {authCxt.getProducts.map((product: IProduct) =>
              showList ? (
                <ProductCard
                  key={product.id}
                  product_name={product.product_name}
                  images={product.images}
                  stock_in_USA={product.stock_in_USA}
                  minimum_order_quantity={product.minimum_order_quantity}
                  price={product.price}
                  product_certification={product.product_certification}
                  stock_in={product.stock_in}
                  category_name={product.category_name}
                />
              ) : (
                <div></div>
              )
            )}
          </div>
        </>
      ) : (
        <h3 className="text-center" style={{ marginTop: "120px" }}>
          No Products Found
        </h3>
      )}
      <Pagination
      pageCount={this.state.pageCount} // total number of pages
      pageRangeDisplayed={5} // number of page numbers to display
      marginPagesDisplayed={2} // number of page numbers to display on either side of the current page
      onPageChange={this.handlePageClick} // function to be called when a page number is clicked
      containerClassName="pagination" // class name for the pagination container
      activeClassName="active" // class name for the active page
      previousLabel="Previous" // label for the "previous" button
      nextLabel="Next" // label for the "next" button
    />
      {/* <Pagination count={10} /> */}
    </>
  );
};
